     3→export type PanelType = 
     4→  | 'workspace-detail'
     5→  | 'call-detail'
     6→  | 'insight-detail'
     7→  | 'filter-tool'
     8→  | 'ai-assistant'
     9→  | 'inspector'
    10→  | null;
    11→
    12→interface PanelState {
    13→  isPanelOpen: boolean;
    14→  panelType: PanelType;
    15→  panelData: any;
    16→  isPinned: boolean;
    17→  panelHistory: Array<{ type: PanelType; data: any }>;
    18→  
    19→  // Actions
    20→  openPanel: (type: PanelType, data?: any) => void;
    21→  closePanel: () => void;
    22→  togglePin: () => void;
    23→  goBack: () => void;
    24→  clearHistory: () => void;
    25→}
    26→
    27→export const usePanelStore = create<PanelState>((set, get) => ({
    28→  isPanelOpen: false,
    29→  panelType: null,
    30→  panelData: null,
    31→  isPinned: false,
    32→  panelHistory: [],
    33→
    34→  openPanel: (type, data = null) => {
    35→    const current = get();
    36→    
    37→    // Add current panel to history if it exists
    38→    if (current.panelType) {
    39→      set({
    40→        panelHistory: [
    41→          ...current.panelHistory,
    42→          { type: current.panelType, data: current.panelData }
    43→        ]
    44→      });
    45→    }
    46→
    47→    set({
    48→      isPanelOpen: true,
    49→      panelType: type,
    50→      panelData: data
    51→    });
    52→  },
    53→
    54→  closePanel: () => {
    55→    const current = get();
    56→    
    57→    // Don't close if pinned
    58→    if (current.isPinned) return;
    59→
    60→    set({
    61→      isPanelOpen: false,
    62→      panelType: null,
    63→      panelData: null,
    64→      panelHistory: []
    65→    });
    66→  },
    67→
    68→  togglePin: () => {
    69→    set((state) => ({ isPinned: !state.isPinned }));
    70→  },
    71→
    72→  goBack: () => {
    73→    const current = get();
    74→    const history = current.panelHistory;
    75→
    76→    if (history.length === 0) {
    77→      // No history, just close
    78→      set({
    79→        isPanelOpen: false,
    80→        panelType: null,
    81→        panelData: null
    82→      });
    83→      return;
    84→    }
    85→
    86→    // Pop last item from history
    87→    const previous = history[history.length - 1];
    88→    const newHistory = history.slice(0, -1);
    89→
    90→    set({
    91→      panelType: previous.type,
    92→      panelData: previous.data,
    93→      panelHistory: newHistory
    94→    });
    95→  },
    96→
    97→  clearHistory: () => {
    98→    set({ panelHistory: [] });
    99→  }
   100→}));
