    20→  defaultOptions: {
    21→    queries: {
    22→      staleTime: 5 * 60 * 1000, // 5 minutes - data stays fresh longer
    23→      gcTime: 10 * 60 * 1000, // 10 minutes - keep unused data in cache
    24→      refetchOnWindowFocus: false, // Prevent refetch on window focus by default
    25→      retry: 1, // Only retry failed requests once
    26→    },
    27→  },
    28→});
